package kokkodis.utils;

public class XYPair {
	private double x;
	private double y;

	public XYPair() {
		// TODO Auto-generated constructor stub
	}

	public XYPair(double i, double j) {
		setX(i);
		setY(j);
	}

	public double getX() {
		return x;
	}

	public void setX(double x) {
		this.x = x;
	}

	public double getY() {
		return y;
	}

	public void setY(double y) {
		this.y = y;
	}

}
